func getFloor(directions string) int {
	var floor int = 0
	for i, s := range strings.Split(directions, "") {
		if s == "(" {
			floor++
		}
		if s == ")" {
			floor--
		}
		if floor == -1 {
			fmt.Printf("Santa is in the basement: %v\n", i+1)
		}
	}
	return floor
}
